import {Text, StyleSheet, View, TextInput} from "react-native";
import React from "react";

const styles = StyleSheet.create(
    {
        container: {
            display: 'flex',
            flexDirection: 'column',
            //backgroundColor: 'lightgrey',
            //flexWrap: 'wrap',
            width: '100%',
            padding: 16,
        },

        labels: {
            fontFamily: 'Roboto',
            fontSize: 18,
            width: 128,
            backgroundColor: 'teal',
            fontWeight: 'bold',
            padding: 8,
            marginBottom: 4,
            width: '100%',
        },

        items:{
            display: 'flex', 
            flexWrap: 'wrap',
            marginBottom: 16
        }


    }
)

const setItems = (id, placeholder, status) =>{
    return <TextInput
        style={{
            backgroundColor: 'lightgray',
            padding: 4,
            margin: 4
        }}
        id = {id}
        placeholder={placeholder}
        status
    />
}

export default class ReportTemplate extends React.Component {
    constructor(props){
        super(props);
        this.state ={
            title: "Relatório Mensal",
            labels:{
                Time: 'Tempo de serviço',
                Hours: 'Horas',
                Minutes: 'Minutos',
                Visits: 'Revisitas',
                Studies: 'Estudos',
                Publications: 'Publicações',
                Videos: 'Videos',
            },
            month: '',
            hours: '',
            munutes: '',
            revisits: '',
            studies: '',
            publications: '',
            videos: ' ',
            remarks: ' '
        };
    }

    render(){
        return <View style={styles.container}>

            <Text style={{fontSize: 24}}>{this.state.title}</Text>
            <Text style={{borderColor: 'black'}} />
            
            <View style={styles.items}>
                <Text style={styles.labels}>
                    {this.state.labels.Time}
                </Text>
                <View style={{width: '40%'}}>
                    <Text style={{
                        color: 'gray', 
                        fontWeight: 'bold',
                        fontSize: 18}}> {this.state.labels.Hours}</Text>
                    
                    setItems()
                    
                    
                    
                    
                    
                    
                </View>
            </View>

            <View style={styles.items}>
                <Text style={styles.labels}>
                    Minutos
                </Text>
                <TextInput
                    placeholder="Minutos de serviço"
                />
            </View>

            <View style={styles.items}>
                <Text style={styles.labels}>
                    Revisitas
                </Text>
                <TextInput
                    placeholder="Número de revisitas"
                />
            </View>

            <View style={styles.items}>
                <Text style={styles.labels}>
                    Estudos
                </Text>
                <TextInput
                    placeholder="Número de estudos"
                />
            </View>
        </View>
    }


}
